/*******************************************************************************
* Function Name  : zonePressed
* Description    : Detecta si se ha producido una pulsación en una zona contreta
* Input          : zone: Estructura con la información de la zona
* Output         : Modifica zone->pressed
*                    0 - si no se detecta pulsación en la zona
*                    1 - si se detecta pulsación en la zona
* Return         : 0 - si no se detecta pulsación en la zona
*                  1 - si se detecta pulsación en la zona
* Attention		  : None
*******************************************************************************/
int8_t zonePressed(struct t_screenZone* zone)
{
	if (pressedTouchPanel == 1) {

		if ((display.x > zone->x) && (display.x < zone->x + zone->size_x) && 
			  (display.y > zone->y) && (display.y < zone->y + zone->size_y))
      {
         zone->pressed = 1;
		   return 1;
      }   
	}
   
	zone->pressed = 0;
	return 0;
}

/*******************************************************************************
* Function Name  : zoneNewPressed
* Description    : Detecta si se ha producido el flanco de una nueva pulsación en 
*                  una zona contreta
* Input          : zone: Estructura con la información de la zona
* Output         : Modifica zone->pressed
*                    0 - si no se detecta pulsación en la zona
*                    1 - si se detecta pulsación en la zona
* Return         : 0 - si no se detecta nueva pulsación en la zona
*                  1 - si se detecta una nueva pulsación en la zona
* Attention		  : None
*******************************************************************************/
int8_t zoneNewPressed(struct t_screenZone* zone)
{
	if (pressedTouchPanel == 1) {

		if ((display.x > zone->x) && (display.x < zone->x + zone->size_x) && 
			  (display.y > zone->y) && (display.y < zone->y + zone->size_y))
      {
         if (zone->pressed == 0)
         {   
            zone->pressed = 1;
            return 1;
         }
		   return 0;
      }
	}

   zone->pressed = 0;
	return 0;
}

void setVelDerecha(float velDcha){
	if(velDcha<0) {                        
		LPC_PWM1->MR2= LPC_PWM1->MR0*(-velDcha)/100; // duty cycle	  
		LPC_GPIO0->FIOCLR3 |= (1<<0) ; LPC_GPIO0->FIOSET2 |= (1<<7);  // in1=1 in2=0
	}
	else { 
		LPC_PWM1->MR2= LPC_PWM1->MR0*(velDcha)/100; // duty cycle
		LPC_GPIO0->FIOCLR2 |= (1<<7) ; LPC_GPIO0->FIOSET3 |= (1<<0); //in1=0 in2=1  porque las ruedas están al revés		
	}
  LPC_PWM1->LER|= (1 << 2)|(1<<0); // PWMLER[0-1]=1
} 

void setVelIzquierda(float velIzqda){ 
	if(velIzqda<0){               //marcha atrás
		LPC_PWM1->MR3= LPC_PWM1->MR0*(-velIzqda)/100; // duty cycle
    LPC_GPIO0->FIOCLR1 |= (1<<3) ;  LPC_GPIO0->FIOSET1 |= (1<<2);  //in3=1 in4=0
	} 
	else{
		LPC_PWM1->MR3= LPC_PWM1->MR0*(velIzqda)/100; // duty cycle
	  LPC_GPIO0->FIOCLR1 |= (1<<2) ;  LPC_GPIO0->FIOSET1 |= (1<<3); // in3=0 in4=1		
	}
  LPC_PWM1->LER|= (1 << 3)|(1<<0); // PWMLER[0-1]=1
}
                                               

void configPWM(void) {           // se usa para los enable de los motores, va a empujones
   LPC_PINCON->PINSEL7|=(3<<18); // P3.25 salida PWM (PWM1.2)
	 LPC_PINCON->PINSEL7|=(3<<20); // P3.26 salida PWM (PWM1.3)
   LPC_SC->PCONP|=(1<<6);
   LPC_PWM1->MR0=Fpclk*Tpwm-1;
   LPC_PWM1->PCR|=(1<<10); //configurado el ENA2 (1.2)
	 LPC_PWM1->PCR|=(1<<11); //configurado el ENA3 (1.3)
   LPC_PWM1->MCR|=(1<<1); //reset en MR0
   LPC_PWM1->TCR|=(1<<0)|(1<<3); //enable contador y pwm
}

