// ISR Timer 2 
void TIMER2_IRQHandler(void) {
	
  static uint32_t  temp;
  static uint32_t  temp2;
  float Lpulso= 2*pi*R/PPV ;
	segundos++; //para controlar que suene 1/20 segundos
	if ((LPC_TIM2->IR & 0x10) == 0x10){ //interrupe por captura 0 ?
		LPC_TIM2->IR |= (1<<4); //borro flag cap0
		N1=LPC_TIM2->CR0 - temp;    // Nº de cuentas de T1TC entre dos flancos consecutivos								
		frecuencia1 = Fpclk/N1;     // frecuencia en Hz.
		temp = LPC_TIM2->CR0;      // almacenamos la captura
		
		if (LPC_GPIO0->FIOPIN & (1<<27)){
       contadorPulsos1++;	
			 velocidad1D=Lpulso*frecuencia1; //procedimiento 1
		 }
		else {
			contadorPulsos1--;
			velocidad1D=-Lpulso*frecuencia1; //procedimiento 1
		}
	}	
	
	if ((LPC_TIM2->IR & 0x20) == 0x20){ //interrupe por captura 1 ?
		LPC_TIM2->IR |= (1<<5); //borro flag cap1	
		N2=LPC_TIM2->CR1 - temp2;    // Nº de cuentas de T1TC entre dos flancos consecutivos								
		frecuencia2 = Fpclk/N2;     // frecuencia en Hz.
		temp2 = LPC_TIM2->CR1;      // almacenamos la captura
		
		if (LPC_GPIO0->FIOPIN & (1<<28)){
       {contadorPulsos2--;}
			 	velocidad1I=-Lpulso*frecuencia2;
		 }
		else {
			contadorPulsos2++;
			velocidad1I=Lpulso*frecuencia2;
    }
	}	
	
	if ((LPC_TIM2->IR & 0x01) == 0x01){ //interrumpe por match cada 100ms ?
		LPC_TIM2->IR |= (1<<0); //borro flag mr0 pág 74
		LPC_TIM2->MR0 = LPC_TIM2->MR0 + N100ms; //incremento MR0 porque el TC no se resetea 
		velocidad2D=Lpulso*contadorPulsos1/T_tim2; //procedimineto 2
	  velocidad2I=Lpulso*contadorPulsos2/T_tim2;
		Lder=contadorPulsos1*Lpulso;//avance por deltaT = 100ms	
		LderT += Lder;  			//avance total
		Lizq=contadorPulsos2*Lpulso;
	  LizqT += Lizq;
		Distancia= (LderT+LizqT)/2;
		posicionDeltaT=(float)((Lizq-Lder)/DisRuedas)*DisGrad;
		posicion += (float)((Lizq-Lder)/DisRuedas)*DisGrad;
		
		if(flag==1) contador--; //para la función pausa
		
		if(posicion>180) posicion = posicion - 360;
		if(posicion<-180) posicion = posicion + 360;
		contadorPulsos1=0;
		contadorPulsos2=0;
	}				
}

void configTimer2(void){
   
    LPC_SC->PCONP|=(1<<22);         // Power ON: Timer 2
    LPC_PINCON->PINSEL0|= (1<<8) | (1<<9);     // p0.04 cap2.0  rueda DERECHA
	  LPC_PINCON->PINSEL0|= (1<<10) | (1<<11);     // p0.05 cap2.1  rueda IZQUIERDA 

    LPC_TIM2->PR  = 0x0;
    LPC_TIM2->MCR |= 0x1;  // interrumpe en MR0 pero no resetea
	  LPC_TIM2->MR0 = Fpclk*T_tim2 - 1; 
    LPC_TIM2->CCR = 0x36;  // que capture en flanco de bajada cap0 y cap1 e interrumpa pág 494
    LPC_TIM2->TCR |= 0x3;    // habilito y reseteo
	  LPC_TIM2->TCR &= ~ (1 << 1); //quito reset 	
		NVIC_EnableIRQ(TIMER2_IRQn);
		NVIC_SetPriority(TIMER2_IRQn,0); 
    NVIC_ClearPendingIRQ(TIMER2_IRQn);
}

