void maquinaEstados(){

	switch (estado){
		static int i=0;	
			
		case 0: //inicio
			if(cadena[i]=='R'){
				a=1;
				LderT=0;
				LizqT=0;
				Distancia=0;
				sentido=0;
				estado=1; //paso a signo
		  }	
			if(cadena[i]=='P'){
				a=0;
				estado=1; //paso a signo
			}
			if(cadena[i]=='D') estado=4; //paso a derecha
			if(cadena[i]=='I') estado=5; //paso a izquierda
			if(cadena[i]=='A') estado=6; //paso a reproducir
			if(((cadena[i]==13) || (cadena[i]==0))&& (flagServer==1)){ //para ver el fin de cadena y borrar la cadena, para UART 13 es enter y para Server /0
			 i=0;	
			 flagServer=0;
			 memset(cadena, 0, sizeof(cadena));
			 memset(buffer, 0, sizeof(buffer));
			 velDerecha=0;
			 velIzquierda=0;
			}	
			break;
		
		case 1: //signo
			i++;
			if((cadena[i]>='0') && (cadena[i]<='9')){
				estado=2; //paso a adquisición
			}
			if (cadena[i]=='-'){
				sentido=1; //sentido negativo
				estado=1;	//vuelvo a signo
			}	
			break;
		
		case 2: //adquisición
			dist=dist*10+cadena[i]-'0';
			i++;
			if((cadena[i]>='0') && (cadena[i]<='9') ) estado=2; //si es número me quedo aquí
			if((cadena[i]<'0') || (cadena[i]>'9')) estado=3;  //si no es número, a ejecución
			break;
		
		case 3: //ejecucuión
		  if(a==1) recto(dist); 
			if(a==0) pausa(dist);
			if(fabs(Distancia)>=dist){
				setVelDerecha(0);
				setVelIzquierda(0);
				dist=0;
				Distancia=0;
				posicion=0;
				estado=0; //vuelvo a inicio
			}
			if(contador==0){
				flag=0;
				dist=0;
				estado=0;  //vuelvo a inicio
			}			
			break;
		
		case 4: //derecha
			angulo=0;
			giro(angulo);
			
			if(posicion>=90){
				setVelIzquierda(0);
				velIzquierda=0;
				i++;
			  posicion=0;
				estado=0; //a inicio				
	  	}
			break;
		
		case 5: //izquierda
			angulo=1;
			giro(angulo);
			if(posicion<=-90){
				setVelDerecha(0);
				velDerecha=0;
				i++;
				posicion=0;
				estado=0; //a inicio
			} 
			break;	
		case 6: //reproducir
			audio=1;//reproduce el audio
			LPC_TIM3->TCR=0x01;		//Start Timer .
		  LPC_TIM3->MR0= Fpclk/8000 -1;
			if(fin==1){
				fin=0;
				i++;	
				estado=0; //a inicio
				LPC_DAC->DACR=0;
				LPC_TIM3->TCR=0x02;	//Stop Timer and reset
			}
  	break;
  }
}

void init_ADC(void)
{	
   LPC_SC->PCONP|= (1<<12);                           // POwer ON
   LPC_PINCON->PINSEL1|= (1<<18);            // (AD0.2)  (P0.25) batería 
   LPC_PINCON->PINMODE1|=(2<<18);           // Deshabilita pullup/pulldown
   LPC_PINCON->PINSEL3|= ((unsigned int)3<<30);                     // (AD0.5) (P1.31) potenciómetro
   LPC_PINCON->PINMODE3|= ((unsigned int)2<<30);                    // Deshabilita pullup/pulldown
   LPC_SC->PCLKSEL0|= (0x00<<8);                      // CCLK/4 (Fpclk después del reset) (100 Mhz/4 = 25Mhz)
   LPC_ADC->ADCR|=(1<<2)|(1<<5)                // canales 2,5
                        | (4<<8)                   // CLKDIV=4   (Fclk_ADC=25Mhz /(4+1)= 5Mhz) para que al mver Umbral no se mueva Bat
                        | (0x01<<21);                 // PDN=1
   // Ojo no se habilita el modo BURST en la inicialización  
   LPC_ADC->ADINTEN=(1<<5);					// Hab. interrupción fin de conversión del ÚLTIMO canal(canal 5)
   NVIC_EnableIRQ(ADC_IRQn);					// 
   NVIC_SetPriority(ADC_IRQn,4);			   //         
}	

