void mando_init(){ //para inicializar, común a todos los mandos negros	
	I2Cdelay();
	I2Cdelay();
	I2CSendAddr(0x52,0);
	I2CSendByte(0xF0);
	I2CSendByte(0x55);
	I2CSendStop();
	I2Cdelay();
	I2CSendAddr(0x52,0);
	I2CSendByte(0xFB);
	I2CSendByte(0x00);
	I2CSendStop();
}


void leerMando(){
	I2CSendAddr(0x52,0);
	I2CSendByte(0x00);
	I2CSendStop();
	I2CSendAddr(0x52,1);
	datoY=I2CGetByte(0); //I2CGetByte(0)^ 0x17 + 0x17
	datoX=I2CGetByte(0);
	datoBasura=I2CGetByte(0);
	datoBasura=I2CGetByte(0);
	datoBasura=I2CGetByte(0);
	datoZC=I2CGetByte(1);
	datoZ=datoZC & 0x01;
	datoC=(datoZC & 0x02) >> 1; //último dato leído	
	I2CSendStop();
	
	
	if(datoX==-1){
		setVelDerecha(60);
		setVelIzquierda(60);
	}
	else if(datoX==1){
		setVelDerecha(-60);
		setVelIzquierda(-60);
	}
	else if (datoY==-1){
		setVelDerecha(0);
		setVelIzquierda(50);
	}
	else if (datoY==1){
		setVelDerecha(50);
		setVelIzquierda(0);
	}
	else{ // como posición central, que no se mueva
		setVelDerecha(0);
		setVelIzquierda(0);
	}
	if(datoZ==0){
		estado=6;
		//LPC_TIM3->TCR=0x01;		//Start Timer .
		//audio=1;//para en el timer sacar el audio
	}
}

void TIMER0_IRQHandler(void) { //cada segundo para leer el mando
	LPC_TIM0->IR = 1<<0;
	if(segundos >= (20/T_tim2)){
		segundos=0;
		LPC_TIM3->TCR=0x01;		//Start Timer.
	 	}
	if(M_manual==1){
		leerMando();
	}
}

